

-- update  list_mat set txt=txt||' mm' where idefix_matskup  = '274';

 select txt,idefix_mat(txt), (string_to_array(txt,' '))[1]  from list_mat   where idefix_matskup  = '274';

update list_mat xx set nazev1 = aa, nazev3 = cc from (
 select idefix , ar[1] aa, ar[array_upper(ar,1)-1]||' '||ar[array_upper(ar,1)] as ccfrom (
 select idefix,idefix_mat(txt), (string_to_array(txt,' ')) as ar from list_mat   where idefix_matskup  = '274'
 ) a ) ax where xx.idefix = ax.idefix;


 update  list_mat set nazev2 =  replace(replace(txt, nazev1,''),nazev3,'')  where idefix_matskup = 274


